body.raw:
{
  "src" : {
    "path" : "/spark/stations/count_evol",
    "axis" : {
      "x" : {
        "name" : "year",
        "format" : "timestamp_year"
      },
      "y" : {
        "name" : "count"
      }
    }
  },
  "dest" : {
    "path" : "/stations/count_evol",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "Evolution of the meteorological stations count in spanish territory",
      "subtitle" : "From 1973 to 2024",
      "x_label" : "Year",
      "y_label" : "Count"
    },
    "figure" : {
      "name" : "Station count",
      "color" : "#4169E1"
    },
    "margin" : {
      "left" : 80,
      "right" : 80,
      "top" : 80,
      "bottom" : 80
    },
    "legend" : {
      "y_offset" : -0.3
    }
  }
}

{
  "src" : {
    "path" : "/spark/interesting_studies/prec_and_press_evol/a_coruna/evol_2024",
    "axis" : {
      "x" : {
        "name" : "date",
        "format" : "timestamp"
      },
      "y_1" : {
        "name" : "prec_daily_sum"
      },
      "y_2" : {
        "name" : "press_daily_avg"
      }
    }
  },
  "dest" : {
    "path" : "/interesting_studies/prec_and_press_evol/a_coruna/evol_2024",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "A CORUÑA - 1387 (A CORUÑA) precipitation and pressure evolution (2024)",
      "subtitle" : "Lat: 432157N. Long: 082517W. Alt: 57",
      "x_label" : "Date",
      "y_1_label" : "Precipitation (mm)",
      "y_2_label" : "Pressure (hPa)"
    },
    "figure_1" : {
      "name" : "Precipitation evolution",
      "color" : "#4169e1"
    },
    "figure_2" : {
      "name" : "Pressure evolution",
      "color" : "#ff0000"
    },
    "margin" : {
      "left" : 80,
      "right" : 80,
      "top" : 80,
      "bottom" : 80
    },
    "legend" : {
      "y_offset" : -0.3
    }
  }
}

{
  "src" : {
    "main" : {
      "path" : "/spark/single_param_studies/temp/evol/a_coruna/evol_yearly_group",
      "axis" : {
        "x" : {
          "name" : "year",
          "format" : "timestamp_year"
        },
        "y" : {
          "name" : "temp_yearly_avg"
        }
      }
    },
    "regression" : {
      "path" : "/spark/single_param_studies/temp/evol/a_coruna/regression",
      "names" : {
        "slope" : "beta_1",
        "intercept" : "beta_0"
      }
    }
  },
  "dest" : {
    "path" : "/single_param_studies/temp/evol/a_coruna/evol_yearly_group",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "A CORUÑA - 1387 (A CORUÑA) temperature evolution (from the beginning of the records)",
      "subtitle" : "Lat: 432157N. Long: 082517W. Alt: 57",
      "x_label" : "Date",
      "y_1_label" : "Temperature (ºC)",
      "y_2_label" : "Temperature (ºC)"
    },
    "figure_1" : {
      "name" : "Temperature evolution",
      "color" : "#4169e1"
    },
    "figure_2" : {
      "name" : "Temperature regression",
      "color" : "#ff0000"
    },
    "margin" : {
      "left" : 120,
      "right" : 120,
      "top" : 100,
      "bottom" : 100
    },
    "legend" : {
      "y_offset" : -0.15
    }
  }
}

{
  "src" : {
    "path" : "/spark/stations/count_by_altitude_2024",
    "names" : {
      "lower_bound" : "min_value",
      "upper_bound" : "max_value",
      "value" : "count"
    }
  },
  "dest" : {
    "path" : "/stations/count_by_altitude_2024",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "Distribution of the station count by altitude",
      "subtitle" : "Year 2024"
    },
    "margin" : {
      "left" : 120,
      "right" : 120,
      "top" : 100,
      "bottom" : 100
    },
    "show_legend" : true
  }
}

{
  "src" : {
    "path" : "/spark/single_param_studies/temp/top_10/highest/2024",
    "axis" : {
      "x" : {
        "name" : "top"
      },
      "y" : {
        "name" : "temp_avg"
      }
    }
  },
  "dest" : {
    "path" : "/single_param_studies/temp/top_10/highest/2024",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "Top 10 stations with highest temperature",
      "subtitle" : "Year 2024",
      "x_label" : "Top",
      "y_label" : "Temperature (ºC)",
      "inside_info" : [ {
        "label" : "Station",
        "build" : [ {
          "text_before" : "",
          "name" : "station_id",
          "text_after" : " "
        }, {
          "text_before" : "",
          "name" : "station_name",
          "text_after" : " "
        }, {
          "text_before" : "(",
          "name" : "state",
          "text_after" : ")."
        } ]
      }, {
        "label" : "Location",
        "build" : [ {
          "text_before" : "",
          "name" : "lat_dms",
          "text_after" : " "
        }, {
          "text_before" : "",
          "name" : "long_dms",
          "text_after" : " "
        }, {
          "text_before" : "(",
          "name" : "altitude",
          "text_after" : " m)."
        } ]
      } ]
    },
    "figure" : {
      "inverted_horizontal_axis" : false,
      "threshold_limit_max_min" : 0.5,
      "threshold_perc_limit_outside_text" : 0.3,
      "range_margin_perc" : 0.05,
      "color" : "#4169e1"
    },
    "margin" : {
      "left" : 120,
      "right" : 120,
      "top" : 100,
      "bottom" : 100
    }
  }
}

{
  "src" : {
    "path" : "/spark/stations/count_by_state_2024",
    "col_names" : [ "state", "count" ]
  },
  "dest" : {
    "path" : "/stations/count_by_state_2024",
    "filename" : "plot",
    "export_png" : false
  },
  "style" : {
    "lettering" : {
      "title" : "Meteorological stations count for each spanish state",
      "subtitle" : "Year 2024",
      "headers" : [ "State", "Station count" ]
    },
    "figure" : {
      "headers" : {
        "align" : "center",
        "color" : "#C8D4E3"
      },
      "cells" : {
        "align" : "center",
        "color" : "#F5F8FF"
      }
    }
  }
}

{
  "src" : {
    "path" : "/spark/climograph/arid/BWh/peninsula/temp_and_prec",
    "axis" : {
      "x" : {
        "name" : "month"
      },
      "y_temp" : {
        "name" : "temp_monthly_avg"
      },
      "y_prec" : {
        "name" : "prec_monthly_sum"
      }
    }
  },
  "dest" : {
    "path" : "/climograph/arid/BWh/peninsula",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "ÁGUILAS - 7002Y (MURCIA) climograph (2024)",
      "subtitle" : "Class: arid - BWh. Lat: 372502N. Long: 013513W. Alt: 32",
      "x_label" : "Month",
      "y_temp_label" : "Temperature (ºC)",
      "y_prec_label" : "Precipitation (mm)"
    },
    "figure_temp" : {
      "name" : "Temperature",
      "color" : "#FF0000"
    },
    "figure_prec" : {
      "name" : "Precipitation",
      "color" : "#4169e1"
    },
    "margin" : {
      "left" : 80,
      "right" : 80,
      "top" : 80,
      "bottom" : 80
    },
    "legend" : {
      "y_offset" : -0.15
    }
  }
}

{
  "src" : {
    "path" : "/spark/single_param_studies/temp/station_records_2024/continental",
    "names" : {
      "longitude" : "long_dec",
      "latitude" : "lat_dec",
      "value" : "temp_avg"
    },
    "location" : "continental"
  },
  "dest" : {
    "path" : "/single_param_studies/temp/heat_map_2024/continental",
    "filename" : "plot",
    "export_png" : true
  },
  "style" : {
    "lettering" : {
      "title" : "Temperature heat map",
      "subtitle" : "Spain continental territory. 2024",
      "long_label" : "Longitude",
      "lat_label" : "Latitude",
      "legend_label" : "Temperature (ºC)",
      "point_info" : [ {
        "label" : "Station",
        "build" : [ {
          "text_before" : "",
          "name" : "station_id",
          "text_after" : " "
        }, {
          "text_before" : "",
          "name" : "station_name",
          "text_after" : " "
        }, {
          "text_before" : "(",
          "name" : "state",
          "text_after" : ")."
        } ]
      }, {
        "label" : "Location",
        "build" : [ {
          "text_before" : "",
          "name" : "lat_dms",
          "text_after" : " "
        }, {
          "text_before" : "",
          "name" : "long_dms",
          "text_after" : " "
        }, {
          "text_before" : "(",
          "name" : "altitude",
          "text_after" : " m)."
        } ]
      } ]
    },
    "figure" : {
      "name" : "temperature heat map",
      "color" : "#000000",
      "color_opacity" : 0.5,
      "point_size" : 3
    },
    "margin" : {
      "left" : 120,
      "right" : 120,
      "top" : 100,
      "bottom" : 100
    }
  }
}
